() => {
  const async = true;

  const applyLoaderSchema = true;

  const type = 'resource';

  const size = 'comfortable';

  const withHeader = false;

  const withCheckbox = false;

  const withPagination = true;

  const page = 1;

  const paginationType = 'jump';

  const pageSize = 12;

  const multipleSorting = false;

  const separator = false;

  let dataAttr = {};
  if (async) {
    dataAttr = {
      fetchData,
    };
  } else {
    dataAttr = {
      schema,
      data,
    };
  }

  return (
    <div
      style={{
        height: '350px',
        // overflow: 'hidden'
      }}
    >
      <Card className="h-100 overflow-hidden">
        <List
          {...dataAttr}
          withHeader={withHeader}
          headerOptions={{
            withSearch: true,
          }}
          withCheckbox={withCheckbox}
          type={type}
          size={size}
          separator={separator}
          withPagination={withPagination}
          paginationType={paginationType}
          page={page}
          pageSize={pageSize}
          loaderSchema={applyLoaderSchema ? loaderSchema : undefined}
          onRowClick={(rowData, rowIndex) =>
            action(`on-row-click:- rowIndex: ${rowIndex} data: ${JSON.stringify(rowData)}`)()
          }
          onSelect={(rowIndex, selected, selectedList) =>
            action(
              `on-select:- rowIndex: ${rowIndex} selected: ${selected} selectedList: ${JSON.stringify(selectedList)}`
            )()
          }
          onPageChange={(newPage) => action(`on-page-change:- ${newPage}`)()}
          multipleSorting={multipleSorting}
          sortingList={[{ name: 'name', type: 'desc' }]}
          filterList={{
            name: ['h-r', 's-z'],
          }}
        />
      </Card>
    </div>
  );
}

headerOptions.description:
Options to be passed if using `withHeader: true`

<pre className="DocPage-codeBlock">
ExternalHeaderProps: {
    children?: React.ReactNode;
    withSearch?: boolean;
    searchPlaceholder?: string;
    dynamicColumn?: boolean;
    allowSelectAll?: boolean;
}
</pre>

| Name | Description | Default |
| --- | --- | --- |
| children | Header actions to be rendered | |
| withSearch | Set to use Search Input | |
| searchPlaceholder | Placeholder of Search Input | "Search" |
| dynamicColumn | Set to use Column controlling dropdown | true |
| allowSelectAll | Set to show Select All button | |

