        "displayName": "Gender",
        "width": 180,
        "resizable": true,
        "cellType": "STATUS_HINT",
        "filters": [
            {
                "label": "Male",
                "value": "male"
            },
            {
                "label": "Female",
                "value": "female"
            }
        ]
    },
    {
        "name": "icon",
        "displayName": "Icon",
        "width": 100,
        "resizable": true,
        "align": "center",
        "cellType": "ICON"
    },
    {
        "name": "customCell",
        "displayName": "Custom Cell",
        "width": 200,
        "resizable": true,
        "cellType": "WITH_META_LIST"
    }
];

  return (
    <div
      className="vh-75"
    >
      <Card className="h-100 overflow-hidden">
        <Table
          loaderSchema={loaderSchema}
          data={data}
          schema={schema}
          withHeader={true}
          headerOptions={{
            withSearch: true
          }}
          onSearch={(currData, searchTerm) => {
            return currData.filter(d =>
              d.firstName.toLowerCase().match(searchTerm.toLowerCase())
              || d.lastName.toLowerCase().match(searchTerm.toLowerCase())
            );
          }}
          withCheckbox={true}
          onSelect={(rowIndex, selected, selectedList, selectAll) => console.log(`on-select:- rowIndex: ${rowIndex} selected: ${selected} selectedList: ${JSON.stringify(selectedList)} selectAll: ${selectAll}`)}
          withPagination={true}
          pageSize={5}
          onPageChange={newPage => console.log(`on-page-change:- ${newPage}`)}
        />
      </Card>
    </div>
  );
};

storySource.source:
() => {
  return (
    <div className="vh-75">
      <Card className="h-100 overflow-hidden">
        <Table
          loaderSchema={loaderSchema}
          data={data}
          schema={editableSchema}
          withHeader={true}
          withCheckbox={true}
          onSelect={(rowIndex, selected, selectedList, selectAll) =>
            action(
              `on-select:- rowIndex: ${rowIndex} selected: ${selected} selectedList: ${JSON.stringify(
                selectedList
              )} selectAll: ${selectAll}`
            )()
          }
          headerOptions={{
            withSearch: true,
          }}
          onSearch={(currData, searchTerm) => {
            return currData.filter(
              (d) =>
                d.firstName.toLowerCase().match(searchTerm.toLowerCase()) ||
                d.lastName.toLowerCase().match(searchTerm.toLowerCase())
            );
          }}
          withPagination={true}
          pageSize={5}
          onPageChange={(newPage) => action(`on-page-change:- ${newPage}`)()}
        />
      </Card>
    </div>
  );
}

